import json
import os
import subprocess
import tempfile


REPO_ROOT = os.path.realpath(os.path.join(os.path.dirname(__file__), "..", ".."))


def deploy_app_code():
    subprocess.run(
        "scripts/kms/js_app_set.sh",
        cwd=REPO_ROOT,
        check=True,
    )


def apply_settings_policy(policy=None):
    subprocess.run(
        "./scripts/kms/settings_policy_set.sh",
        env={
            **os.environ,
            **({"SETTINGS_POLICY": json.dumps(policy)} if policy is not None else {})
        },
        cwd=REPO_ROOT,
        check=True,
    )


def apply_kms_constitution():
    subprocess.run(
        [
            "./scripts/kms/constitution_set.sh",
            "./governance/constitution/kms_actions_maa.js",
        ],
        cwd=REPO_ROOT,
        check=True,
    )


def apply_key_release_policy():
    subprocess.run(
        [
            "scripts/kms/release_policy_set.sh",
            "governance/proposals/set_key_release_policy_maa_add.json",
        ],
        cwd=REPO_ROOT,
        check=True,
    )


def remove_key_release_policy():
    subprocess.run(
        [
            "scripts/kms/release_policy_set.sh",
            "governance/proposals/set_key_release_policy_maa_remove.json",
        ],
        cwd=REPO_ROOT,
        check=True,
    )


def trust_jwt_issuer(iss=""):
    command = ["scripts/kms/jwt_issuer_trust.sh"]
    if iss:
        command.extend(["--iss", iss])  # Pass '--iss' and 'iss' as separate arguments
    subprocess.run(
        command,
        cwd=REPO_ROOT,
        check=True,
    )
    

